//binary gradient- returns a two colors gradient with stops dependent on percents


//for svg that needs one gradient

export const basicGradient = (percent, datum, color1, color2) => {
    return (
        <linearGradient
            id={`gradient${datum}`}
            key={`gradient${datum}`}
            x1="0%"
            y1="0%"
            x2="0%"
            y2="100%"
        >
            <stop
                offset="0%"
                stopColor={color1}
            />
            <stop
                offset={`${Math.round(percent)}%`}
                stopColor={color1}
            />
            <stop
                offset={`${Math.round(percent)}%`}
                stopColor={color2}
            />
            <stop
                offset="100%"
                stopColor={color2}
            />
        </linearGradient>
    )
}

//for svg that needs more than one gradient
export const binaryGradient = (array, percents, year, color1, color2) => {

    return (
        <>
            {array.map((item, i) => (
                <linearGradient
                    id={`gradient${year}${i}`}
                    key={`gradient${year}${i}`}
                    x1="0%"
                    y1="0%"
                    x2="0%"
                    y2="100%"
                >
                    <stop
                        offset="0%"
                        stopColor={color1}
                    />
                    <stop
                        offset={`${Math.round(percents[i])}%`}
                        stopColor={color1}
                    />
                    <stop
                        offset={`${Math.round(percents[i])}%`}
                        stopColor={color2}
                    />
                    <stop
                        offset="100%"
                        stopColor={color2}
                    />
                </linearGradient>
            ))}
        </>
    )
}

//gradient for 5 types of courts (5 colors, specific conditional logic)


export const courtsGradient = (percents, datum, courtsHE, colorScale) => {

    //helper function and variable for calculating offsets for colors

    const combinedPercents = [];

    percents.forEach((percent, i) => {
        if (i === 0) {
            combinedPercents.push(Number(percent))

        } else {
            combinedPercents.push(Number(percent) + Number(combinedPercents[i - 1]))

        }
    });

    return (
        <>

            <linearGradient
                id={`gradient${datum}`}
                key={`gradientCourts${datum}`}
                x1="0%"
                y1="0%"
                x2="0%"
                y2="100%"
            >
                {/* magistrate */}
                <stop
                    offset="0%"
                    stopColor={colorScale(courtsHE[0])}
                />
                <stop
                    offset={`${Math.round(combinedPercents[0])}%`}
                    stopColor={colorScale(courtsHE[0])}
                />
                {/* district */}
                <stop
                    offset={`${Math.round(combinedPercents[0])}%`}
                    stopColor={colorScale(courtsHE[1])}
                />
                <stop
                    offset={`${Math.round(combinedPercents[1])}%`}
                    stopColor={colorScale(courtsHE[1])}
                />
                {/* supreme */}
                <stop
                    offset={`${Math.round(combinedPercents[1])}%`}
                    stopColor={colorScale(courtsHE[2])}
                />

                {datum > 1969 ?
                    (<>
                        {/* supreme */}
                        <stop
                            offset={`${Math.round(combinedPercents[2])}%`}
                            stopColor={colorScale(courtsHE[2])}
                        />
                        {/* labor regional */}
                        <stop
                            offset={`${Math.round(combinedPercents[2])}%`}
                            stopColor={colorScale(courtsHE[3])}
                        />
                        <stop
                            offset={`${Math.round(combinedPercents[3])}%`}
                            stopColor={colorScale(courtsHE[3])}
                        />
                        {/* labor national */}
                        <stop
                            offset={`${Math.round(combinedPercents[3])}%`}
                            stopColor={colorScale(courtsHE[4])}
                        />
                        <stop
                            offset="100%"
                            stopColor={colorScale(courtsHE[4])}
                        />
                    </>
                    ) :
                    (
                        <stop
                            offset="100%"
                            stopColor={colorScale(courtsHE[2])}
                        />

                    )
                }
            </linearGradient>
        </>
    )
}
